mapper.select:
		{ CALL
			DECLARE
				p_idx  INT;
				p_crea_id VARCHAR2(20);
				p_rc   SYS_REFCURSOR;
			BEGIN
				p_idx  := #{idx};
				p_crea_id := #{crea_id};

				OPEN p_rc FOR
                    SELECT IDX 
					       , PARENT_IDX 
					       , TITLE 
					       , CONTENTS
					       , HIT_CNT 
					       , DEL_GB 
					       , CREA_DTM 
					       , CREA_ID 
                      FROM TB_BOARD
                     WHERE IDX >= p_idx
                       AND CREA_ID like p_crea_id||'%'
                  ORDER BY IDX DESC;

                #{resultList, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=procVO2} := p_rc;

                #{crea_id, mode=OUT, jdbcType=VARCHAR, javaType=String} := 'call anonymous block....';
			END
		}
